import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import '../../styles/styles.css';
import logoFSPH from '../../assets/fsph_logo.png';
import menuIcon from '../../assets/menu_hamburger.png';
import cadIcon from '../../assets/icons/cadastros.png';
import recepIcon from '../../assets/icons/recepcao.png';
import ambIcon from '../../assets/icons/ambulatorio.png';
import labIcon from '../../assets/icons/laboratorio.png';
import logoutIcon from '../../assets/icons/logout_icon.png';
import { AuthContext } from '../../App';
import { pacientesMock, estoqueMock } from '../../data/mocks';

export default function ProntuarioEletronico() {
  const { setIsAuthenticated } = React.useContext(AuthContext);
  const navigate = useNavigate();
  const location = useLocation();
  const [menuOpen, setMenuOpen] = useState(false);
  const [imagemPreview, setImagemPreview] = useState(null);
  const [nomeDocumento, setNomeDocumento] = useState("");
  const [form, setForm] = useState({
    nomePaciente: '',
    idadePaciente: '',
    cpfPaciente: '',
    anamnese: '',
    diagnostico: '',
    prescricao: '',
    encaminhamento: '',
    medicamento: '',
  });
  const [errors, setErrors] = useState({});

  useEffect(() => {
    const params = new URLSearchParams(location.search);
    const cpf = params.get('cpf');
    if (cpf) {
      const paciente = pacientesMock.find(p => p.cpf === cpf);
      if (paciente) {
        setForm(prev => ({
          ...prev,
          nomePaciente: paciente.nome,
          idadePaciente: paciente.idade,
          cpfPaciente: paciente.cpf,
        }));
      }
    }
  }, [location]);

  const validateForm = () => {
    const newErrors = {};
    if (!form.nomePaciente) newErrors.nomePaciente = 'Nome é obrigatório';
    if (!form.idadePaciente || isNaN(form.idadePaciente) || form.idadePaciente < 0) newErrors.idadePaciente = 'Idade inválida';
    if (!form.cpfPaciente || !/^\d{11}$/.test(form.cpfPaciente.replace(/\D/g, ''))) newErrors.cpfPaciente = 'CPF inválido';
    if (!form.anamnese) newErrors.anamnese = 'Anamnese é obrigatória';
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleInputChange = (e) => {
    const { id, value } = e.target;
    setForm(prev => ({ ...prev, [id]: value }));
  };

  const handleImagemChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const imageUrl = URL.createObjectURL(file);
      setImagemPreview(imageUrl);
    }
  };

  const handleDocumentoChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setNomeDocumento(file.name);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (validateForm()) {
      alert('Prontuário salvo com sucesso!');
      setForm({
        nomePaciente: '',
        idadePaciente: '',
        cpfPaciente: '',
        anamnese: '',
        diagnostico: '',
        prescricao: '',
        encaminhamento: '',
        medicamento: '',
      });
      setImagemPreview(null);
      setNomeDocumento('');
    }
  };

  const handleVoltar = () => {
    navigate(-1);
  };

  const apenasNumeros = (e) => {
    e.target.value = e.target.value.replace(/[^0-9]/g, '');
  };

  const handleLogout = () => {
    setIsAuthenticated(false);
    navigate('/login');
  };

  return (
    <div className="container">
      <aside className="sidebar-fixed-left">
        <div className="logo-container-fixed">
          <img src={logoFSPH} alt="Logo FSPH" className="logo-fundo-saude" />
        </div>
        <nav className="menu-navegacao-esquerda">
          <h2 className="menu-navegacao-titulo">3. Ambulatório</h2>
          <ul>
            <li><Link to="/prontuario">3.1 Prontuário Eletrônico</Link></li>
            <li><Link to="/evo-ambulatorio">3.2 Evolução</Link></li>
            <li><Link to="/evo-visualizar">3.3 Visualizar Evoluções</Link></li>
          </ul>
        </nav>
      </aside>

      <main className="main-content">
        <header className="main-header">
          <h1>Prontuário Eletrônico</h1>
          <button className="hamburger-button" onClick={() => setMenuOpen(!menuOpen)}>
            <img src={menuIcon} alt="Menu" />
          </button>
        </header>

        <section className="form-cadastro-container">
          <form onSubmit={handleSubmit}>
            <div className="form-row">
              <div className="form-group nome-usuario">
                <label htmlFor="nomePaciente">Nome do Paciente:</label>
                <input
                  type="text"
                  id="nomePaciente"
                  value={form.nomePaciente}
                  onChange={handleInputChange}
                  placeholder="Digite o nome do paciente"
                  maxLength="100"
                />
                {errors.nomePaciente && <span className="error">{errors.nomePaciente}</span>}
              </div>
              <div className="form-group data-nasc">
                <label htmlFor="idadePaciente">Idade:</label>
                <input
                  type="text"
                  id="idadePaciente"
                  value={form.idadePaciente}
                  onChange={handleInputChange}
                  placeholder="Digite sua Idade"
                  maxLength="3"
                  onInput={apenasNumeros}
                />
                {errors.idadePaciente && <span className="error">{errors.idadePaciente}</span>}
              </div>
              <div className="form-group cpf">
                <label htmlFor="cpfPaciente">CPF:</label>
                <input
                  type="text"
                  id="cpfPaciente"
                  value={form.cpfPaciente}
                  onChange={handleInputChange}
                  placeholder="00000000000"
                  maxLength="11"
                  onInput={apenasNumeros}
                />
                {errors.cpfPaciente && <span className="error">{errors.cpfPaciente}</span>}
              </div>
            </div>

            <div className="form-row">
              <div className="form-group" style={{ flexBasis: "100%" }}>
                <label htmlFor="anamnese">Anamnese:</label>
                <textarea id="anamnese" value={form.anamnese} onChange={handleInputChange} rows="3" />
                {errors.anamnese && <span className="error">{errors.anamnese}</span>}
              </div>
            </div>

            <div className="form-row">
              <div className="form-group" style={{ flexBasis: "100%" }}>
                <label htmlFor="diagnostico">Diagnóstico:</label>
                <textarea id="diagnostico" value={form.diagnostico} onChange={handleInputChange} rows="2" />
              </div>
            </div>

            <div className="form-row">
              <div className="form-group" style={{ flexBasis: "100%" }}>
                <label htmlFor="prescricao">Prescrição:</label>
                <select
                  id="medicamento"
                  value={form.medicamento}
                  onChange={handleInputChange}
                >
                  <option value="">Selecione um medicamento</option>
                  {estoqueMock
                    .filter(item => item.category === 'Medicamento')
                    .map(item => (
                      <option key={item.id} value={item.name}>{item.name} ({item.quantity} disponíveis)</option>
                    ))
                  }
                </select>
                <textarea id="prescricao" value={form.prescricao} onChange={handleInputChange} rows="2" />
              </div>
            </div>

            <div className="form-row">
              <div className="form-group" style={{ flexBasis: "100%" }}>
                <label htmlFor="encaminhamento">Encaminhamento (Exames/Enfermagem):</label>
                <textarea id="encaminhamento" value={form.encaminhamento} onChange={handleInputChange} rows="2" />
              </div>
            </div>

            <div className="form-actions">
              <button type="submit" className="submit-button-main">Salvar</button>
              <button type="button" className="submit-button-main" style={{ background: "#6c757d", marginLeft: "10px" }} onClick={handleVoltar}>Voltar</button>

              <label htmlFor="anexoExame" className="submit-button-main" style={{ background: "#1e3a8a", marginLeft: "10px", cursor: "pointer" }}>
                Anexar Imagem
                <input type="file" id="anexoExame" name="anexoExame" accept="image/*" style={{ display: "none" }} onChange={handleImagemChange} />
              </label>

              {imagemPreview && (
                <img src={imagemPreview} alt="Prévia do exame" style={{ width: 48, height: 48, objectFit: "cover", marginLeft: "10px", borderRadius: 4, border: "1px solid #ccc" }} />
              )}

              <label htmlFor="anexoDocumento" className="submit-button-main" style={{ background: "#2563eb", marginLeft: "10px", cursor: "pointer" }}>
                Anexar Documento
                <input type="file" id="anexoDocumento" name="anexoDocumento" accept=".pdf,.doc,.docx,.txt,.odt" style={{ display: "none" }} onChange={handleDocumentoChange} />
              </label>
            </div>

            {nomeDocumento && (
              <div style={{ marginTop: "10px", marginLeft: "10px", color: "#1e3a8a", fontWeight: "bold" }}>{nomeDocumento}</div>
            )}
          </form>
        </section>
      </main>

      <aside className={`popup-menu-right ${menuOpen ? 'open' : ''}`}>
        <ul>
          <li><Link to="#"><img src={cadIcon} alt="" /> Cadastros</Link></li>
          <li><Link to="/recepcao"><img src={recepIcon} alt="" /> Recepção</Link></li>
          <li><Link to="/evo-ambulatorio"><img src={ambIcon} alt="" /> Ambulatório</Link></li>
          <li><Link to="/estoque"><img src={labIcon} alt="" /> Estoque</Link></li>
        </ul>
        <ul>
          <li><Link to="/login" onClick={handleLogout}><img src={logoutIcon} alt="Sair" /> Sair</Link></li>
        </ul>
      </aside>

      {menuOpen && <div className="modal-fsph-overlay" onClick={() => setMenuOpen(false)}></div>}
    </div>
  );
}
